function wyswietlOferty(e){const t=document.getElementById("offers-list");t.innerHTML="",e.forEach(((e,n)=>{const a=document.createElement("div");a.classList.add("offer");const r=document.createElement("form");r.innerHTML=`\n            <label for="brand-${n}">Marka:</label>\n            <input type="text" id="brand-${n}" name="brand" value="${e.brand}" required>\n\n            <label for="type-${n}">Typ naczepy:</label>\n            <input type="text" id="type-${n}" name="type" value="${e.type}" required>\n\n            <label for="price-${n}">Cena (zł/miesiąc):</label>\n            <input type="text" id="price-${n}" name="price" value="${e.price}" required>\n\n            <label for="year-${n}">Rok produkcji:</label>\n            <input type="text" id="year-${n}" name="year" value="${e.year}" required>\n\n            <label for="vin-${n}">VIN:</label>\n            <input type="text" id="vin-${n}" name="vin" value="${e.vin}" required>\n\n            <label for="images-${n}">Zdjęcia (URL, oddzielone przecinkami bez spacji):</label>\n            <input type="text" id="images-${n}" name="images" value="${e.images.join(",")}" required>\n\n            <button type="button" onclick="edycjaOferty(${n})">Zapisz zmiany</button>\n            <button type="button" onclick="usunOferte(${n})">Usuń ofertę</button>\n        `,a.appendChild(r),t.appendChild(a)}))}function edycjaOferty(e){const t={brand:document.getElementById(`brand-${e}`).value,type:document.getElementById(`type-${e}`).value,price:document.getElementById(`price-${e}`).value,year:document.getElementById(`year-${e}`).value,vin:document.getElementById(`vin-${e}`).value,images:document.getElementById(`images-${e}`).value.split(",")};let n=JSON.parse(localStorage.getItem("offers"))||[];n[e]=t,localStorage.setItem("offers",JSON.stringify(n)),wyswietlOferty(n),alert("Zmiany zostały zapisane.")}function usunOferte(e){let t=JSON.parse(localStorage.getItem("offers"))||[];t.splice(e,1),localStorage.setItem("offers",JSON.stringify(t)),wyswietlOferty(t),alert("Oferta została usunięta.")}document.addEventListener("DOMContentLoaded",(function(){const e=JSON.parse(localStorage.getItem("offers"))||[];e.length>0&&wyswietlOferty(e)})),document.getElementById("add-offer-form").addEventListener("submit",(function(e){e.preventDefault();const t={brand:document.getElementById("brand").value,type:document.getElementById("type").value,price:document.getElementById("price").value,year:document.getElementById("year").value,vin:document.getElementById("vin").value,images:document.getElementById("images").value.split(",")};let n=JSON.parse(localStorage.getItem("offers"))||[];n.push(t),localStorage.setItem("offers",JSON.stringify(n)),document.getElementById("add-offer-form").reset(),alert("Nowa oferta została dodana.")})),document.getElementById("clear-offers-button").addEventListener("click",(function(){localStorage.removeItem("offers"),alert("Wszystkie ogłoszenia zostały usunięte.")}));